=import React from 'react';
import { Users, Shield, Zap, BarChart, Clock, Trophy, TrendingDown, AlertTriangle, DollarSign, Ban, Target, Calendar } from 'lucide-react';
import { HeroSection } from '../components/HeroSection';
import { ProblemSection } from '../components/ProblemSection';
import { SolutionSection } from '../components/SolutionSection';
import { MidPageCTA } from '../components/MidPageCTA';
import { MarketChallengesSection } from '../components/MarketChallengesSection';
import { OperationalIssuesSection } from '../components/OperationalIssuesSection';
import { SocialProofSection } from '../components/SocialProofSection';
import { FAQSection } from '../components/FAQSection';
import { CalendlySection } from '../components/CalendlySection';

export default function HomePage() {
  const scrollToCalendly = () => {
    const element = document.getElementById('calendly-section');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="min-h-screen bg-white">
      <HeroSection
        title="{{ $('Hero Section').item.json.output.title }}"
        highlightedText="{{ $('Hero Section').item.json.output.highlightedText }}"
        subtitle="{{ $('Hero Section').item.json.output.subtitle }}"
        badgeText="{{ $('Hero Section').item.json.output.badgeText }}"
        primaryButtonText="{{ $('Hero Section').item.json.output.primaryButtonText }}"
        onButtonClick={scrollToCalendly}
        trustText={{ `{{main: "` }}{{ $('Hero Section').item.json.output.trustText.main }}", sub: "{{ $('Hero Section').item.json.output.trustText.sub }}"{{ `\}\}`}}{{ `stats={{ value: "` }}{{ $('Hero Section').item.json.output.stats.value }}", label: "{{ $('Hero Section').item.json.output.stats.label }}"}}


        heroImage={{ `{{` }}
          src: "https://images.unsplash.com/photo-1553877522-43269d4ea984?auto=format&fit=crop&w=800&q=80",
          alt: "Business Growth"
        }}
      />

      <ProblemSection
        title="{{ $('Problem Section').item.json.output.title }}"
        problems={[
          { icon: Users, title: "{{ $('Problem Section').item.json.output.problems[0].title }}", description: "{{ $('Problem Section').item.json.output.problems[0].description }}" },
          { icon: Shield, title: "{{ $('Problem Section').item.json.output.problems[1].title }}", description: "{{ $('Problem Section').item.json.output.problems[1].description }}" },
          { icon: Zap, title: "{{ $('Problem Section').item.json.output.problems[2].title }}", description: "{{ $('Problem Section').item.json.output.problems[2].description }}" }
        ]}
      />

      <SolutionSection
        title="{{ $('Solution Section').item.json.output.title }}"
        solutions={[
          "{{ $('Solution Section').item.json.output.solutions[0] }}",
          "{{ $('Solution Section').item.json.output.solutions[1] }}",
          "{{ $('Solution Section').item.json.output.solutions[2] }}"
        ]}

        image={{ `{{` }}
          src: "https://images.unsplash.com/photo-1552664730-d307ca884978?auto=format&fit=crop&w=800&q=80",
          alt: "Business Solution"
        }}
        stats={{ `{{` }}
          value: "{{ $('Solution Section').item.json.output.stats.value }}",
          label: "{{ $('Solution Section').item.json.output.stats.label }}"
        }}
      />

      <MidPageCTA
        title="{{ $('Midpage CTA').item.json.output.title }}"
        subtitle="{{ $('Midpage CTA').item.json.output.subtitle }}"
        ctaButton={{ `{{` }}
          text: "{{ $('Midpage CTA').item.json.output.ctaButton.text }}",
          subtext: "{{ $('Midpage CTA').item.json.output.subtitle }}"
        }}
        onButtonClick={scrollToCalendly}
        backgroundImage="https://images.unsplash.com/photo-1552664730-d307ca884978?auto=format&fit=crop&w=2000&q=80"
      />

      <MarketChallengesSection
        title="{{ $('Market Challenge Section').item.json.output.title }}"
        subtitle="{{ $('Market Challenge Section').item.json.output.subtitle }}"
        challenges={[
          {
            icon: TrendingDown,
            title: "{{ $('Market Challenge Section').item.json.output.challenges[0].title }}",
            description: "{{ $('Market Challenge Section').item.json.output.challenges[0].description }}"
          },
          {
            icon: AlertTriangle,
            title: "{{ $('Market Challenge Section').item.json.output.challenges[1].title }}",
            description: "{{ $('Market Challenge Section').item.json.output.challenges[1].description }}"
          },
          {
            icon: DollarSign,
            title: "{{ $('Market Challenge Section').item.json.output.challenges[2].title }}",
            description: "{{ $('Market Challenge Section').item.json.output.challenges[2].description }}"
          }
        ]}
        backgroundImage="https://images.unsplash.com/photo-1557804506-669a67965ba0?auto=format&fit=crop&w=2000&q=80"
      />

      <OperationalIssuesSection
        title="{{ $('Operational Issues Section').item.json.output.title }}"
        subtitle="{{ $('Operational Issues Section').item.json.output.subtitle }}"
        issues={[
          {
            icon: Clock,
            title: "{{ $('Operational Issues Section').item.json.output.challenges[0].title }}",
            description: "{{ $('Operational Issues Section').item.json.output.challenges[0].description }}"
          },
          {
            icon: Ban,
            title: "{{ $('Operational Issues Section').item.json.output.challenges[1].title }}",
            description: "{{ $('Operational Issues Section').item.json.output.challenges[1].description }}"
          },
          {
            icon: Target,
            title: "{{ $('Operational Issues Section').item.json.output.challenges[2].title }}",
            description: "{{ $('Operational Issues Section').item.json.output.challenges[2].impact }}"
          }
        ]}
      />

      <SocialProofSection
        title="{{ $('Social Proof Section').item.json.output.title }}"
        subtitle="{{ $('Social Proof Section').item.json.output.subtitle }}"
        testimonials={[
          {
            name: "{{ $('Social Proof Section').item.json.output.testimonials[0].name }}",
            role: "{{ $('Social Proof Section').item.json.output.testimonials[0].role }}",
            content: "{{ $('Social Proof Section').item.json.output.testimonials[0].content }}",
            image: {
              src: "https://images.unsplash.com/photo-1494790108377-be9c29b29330?auto=format&fit=crop&w=400&q=80",
              alt: "{{ $('Social Proof Section').item.json.output.testimonials[0].name }}"
            }
          },
          {
            name: "{{ $('Social Proof Section').item.json.output.testimonials[1].name }}",
            role: "{{ $('Social Proof Section').item.json.output.testimonials[1].role }}",
content: "{{ $('Social Proof Section').item.json.output.testimonials[2].content }}",
            image: {
              src: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?auto=format&fit=crop&w=400&q=80",
              alt: "{{ $('Social Proof Section').item.json.output.testimonials[1].name }}"
            }
          },
          {
            name: "{{ $('Social Proof Section').item.json.output.testimonials[2].name }}",
            role: "{{ $('Social Proof Section').item.json.output.testimonials[2].role }}",
            content: "{{ $('Social Proof Section').item.json.output.testimonials[2].content }}",
            image: {
              src: "https://images.unsplash.com/photo-1487412720507-e7ab37603c6f?auto=format&fit=crop&w=400&q=80",
              alt: "{{ $('Social Proof Section').item.json.output.testimonials[2].name }}"
            }
          }
        ]}
        backgroundImage="https://images.unsplash.com/photo-1557804506-669a67965ba0?auto=format&fit=crop&w=2000&q=80"
      />

      <FAQSection
        title="{{ $('FAQ Section').item.json.output.title }}"
        subtitle="{{ $('FAQ Section').item.json.output.subtitle }}"
        backgroundImage="https://images.unsplash.com/photo-1557804506-669a67965ba0?auto=format&fit=crop&w=2000&q=80"
        gradientColors={{ `{{` }}
          from: "blue-600",
          to: "purple-600"
        }}
        decorativeColors={{ `{{` }}
          first: "bg-blue-200",
          second: "bg-purple-200",
          third: "bg-pink-200"
        }}
        faqs={[
          {
            question: "{{ $('FAQ Section').item.json.output.faqs[0].question }}",
            answer: "{{ $('FAQ Section').item.json.output.faqs[0].answer }}"
          },
          {
            question: "{{ $('FAQ Section').item.json.output.faqs[1].question }}",
            answer: "{{ $('FAQ Section').item.json.output.faqs[1].answer }}"
          },
          {
            question: "{{ $('FAQ Section').item.json.output.faqs[2].question }}",
            answer: "{{ $('FAQ Section').item.json.output.faqs[2].answer }}"
          },
          {
            question: "{{ $('FAQ Section').item.json.output.faqs[3].question }}",
            answer: "{{ $('FAQ Section').item.json.output.faqs[3].answer }}"
          },
          {
            question: "{{ $('FAQ Section').item.json.output.faqs[4].question }}",
            answer: "{{ $('FAQ Section').item.json.output.faqs[4].answer }}"
          },
          {
            question: "{{ $('FAQ Section').item.json.output.faqs[5].question }}",
            answer: "{{ $('FAQ Section').item.json.output.faqs[5].answer }}"
          }
        ]}
      />

      <CalendlySection
        id="calendly-section"
        title="{{ $json.output.title }}"
        subtitle="{{ $json.output.subtitle }}"
        badgeIcon={Clock}
        badgeText="{{ $json.output.badgeText }}"
        features={[
          {
            icon: Calendar,
            title: "Flexible Scheduling",
            description: "Choose a time that works best for you"
          },
          {
            icon: Clock,
            title: "15-Minute Session",
            description: "Focused discussion on your business needs"
          },
          {
            icon: Users,
            title: "Expert Consultation",
            description: "Meet with our senior growth strategist"
          }
        ]}
        calendlyUrl="{{ $('On form submission').item.json['calendly link'] }}"
        backgroundImage="https://images.unsplash.com/photo-1557804506-669a67965ba0?auto=format&fit=crop&w=2000&q=80"
      />
    </div>
  );
}
